  function addUserHandler(user) {
    setUsers((prevUsers) => {
      return [user, ...prevUsers];
    });
    console.log(users);
  }

  return (
    <div>
      <AddUser onAddUser={addUserHandler} />
      //<Users allUsers={users} />
    </div>
  );
}

export default App;
