  const [users, setUsers] = useState("");

  function addUserHandler(user){
    setUsers(prevUsers => {
      return [user, ...prevUsers]
    });
    console.log(users)
  }

  return (
    <div>
      <AddUser onAddUser={addUserHandler}/>
    </div>
  );
}

export default App;
